import XLSX from 'xlsx';
import fs from 'fs';

// Fun√ß√£o para converter data serial do Excel para Date
function excelDateToJSDate(excelDate) {
  if (!excelDate || typeof excelDate !== 'number') return null;

  // Excel date serial: dias desde 1900-01-01 (com corre√ß√£o para bug do Excel)
  const excelEpoch = new Date(1900, 0, 1);
  const jsDate = new Date(excelEpoch.getTime() + (excelDate - 2) * 24 * 60 * 60 * 1000);
  return jsDate.toISOString().split('T')[0]; // formato YYYY-MM-DD
}

// Fun√ß√£o para extrair ano da data
function getYearFromDate(dateString) {
  if (!dateString) return null;
  return new Date(dateString).getFullYear();
}

// Fun√ß√£o para corrigir classifica√ß√£o de departamentos
function corrigirDepartamento(departamento, unidade) {
  // DRFR e DRFRV s√£o delegacias especializadas, n√£o departamentos
  // Devem ser reclassificadas para DEIC (Departamento Especializado de Investiga√ß√µes Criminais)
  if (departamento === 'DRFR' || departamento === 'DRFRV') {
    return 'DEIC';
  }
  return departamento;
}

// Fun√ß√£o para processar os dados das planilhas
function processExcelData() {
  console.log('Importando dados das planilhas Excel...\n');

  // Usar arquivo mais recente dispon√≠vel no sistema
  const workbook1 = XLSX.readFile('attached_assets/CONCLUSIVA 2 - PRAZO_1753189803746.xlsx');
  const worksheet1 = workbook1.Sheets['Gest√£o Inspe√ß√µes'];
  const data1 = XLSX.utils.sheet_to_json(worksheet1, { header: 1 });

  // Planilha sem prazos
  const workbook2 = XLSX.readFile('attached_assets/CONCLUSIVA_1753189803747.xlsx');
  const worksheet2 = workbook2.Sheets['Gest√£o Inspe√ß√µes'];
  const data2 = XLSX.utils.sheet_to_json(worksheet2, { header: 1 });

  console.log(`Planilha 1 (com prazos): ${data1.length - 1} registros`);
  console.log(`Planilha 2 (sem prazos): ${data2.length - 1} registros`);

  const inspections = [];

  // Processar primeira planilha (com prazos) - TODOS os dados
  for (let i = 1; i < data1.length; i++) {
    const row = data1[i];
    if (!row || row.length < 10) continue;

    const inspection = {
      id: inspections.length + 1,
      numero: String(row[0] || i).padStart(3, '0'),
      unidadeInspecionada: row[1] || 'N/A',
      departamento: corrigirDepartamento(row[2] || 'N/A', row[1] || 'N/A'),
      coorpin: row[3] || 'N/A',
      dataInspecao: excelDateToJSDate(row[4]),
      delegadoCorregedor: row[5] || 'N/A',
      naoConformidade: row[6] || 'N/A',
      descricaoNaoConformidade: row[7] || 'N/A',
      providenciasIniciais: row[8] || 'Pendente',
      providenciasIntermediarias: row[8] || 'Pendente',
      providenciasConclusivas: row[8] || 'Pendente',
      dataInicioRegularizacao: excelDateToJSDate(row[9]),
      diasPrazo: row[10] || 0,
      dataFimRegularizacao: excelDateToJSDate(row[11]),
      statusPrazo: row[12] || 'Pendente',
      dataNovaInspecao: excelDateToJSDate(row[13]),
      criticidade: row[14] || 'M√©dia'
    };

    inspections.push(inspection);
  }

  // Processar segunda planilha (sem prazos) - TODOS os dados
  for (let i = 1; i < data2.length; i++) {
    const row = data2[i];
    if (!row || row.length < 8) continue;

    const inspection = {
      id: inspections.length + 1,
      numero: String(row[0] || inspections.length + 1).padStart(3, '0'),
      unidadeInspecionada: row[1] || 'N/A',
      departamento: corrigirDepartamento(row[2] || 'N/A', row[1] || 'N/A'),
      coorpin: row[3] || 'N/A',
      dataInspecao: excelDateToJSDate(row[4]),
      delegadoCorregedor: row[5] || 'N/A',
      naoConformidade: row[6] || 'N/A',
      descricaoNaoConformidade: row[7] || 'N/A',
      providenciasIniciais: row[8] || 'Pendente',
      providenciasIntermediarias: row[8] || 'Pendente',
      providenciasConclusivas: row[8] || 'Pendente',
      dataInicioRegularizacao: null,
      diasPrazo: 0,
      dataFimRegularizacao: null,
      statusPrazo: 'Pendente',
      dataNovaInspecao: null,
      criticidade: row[9] || 'M√©dia'
    };

    inspections.push(inspection);
  }

  console.log(`\nTotal de inspe√ß√µes processadas: ${inspections.length}`);

  // Salvar dados em formato JSON
  fs.writeFileSync('data/real-inspections.json', JSON.stringify(inspections, null, 2));
  console.log('Dados salvos em: data/real-inspections.json');

  // Mostrar estat√≠sticas
  const departments = [...new Set(inspections.map(i => i.departamento))];
  const criticidades = inspections.reduce((acc, i) => {
    acc[i.criticidade] = (acc[i.criticidade] || 0) + 1;
    return acc;
  }, {});

  // Estat√≠sticas por ano
  const anoStats = inspections.reduce((acc, i) => {
    const ano = getYearFromDate(i.dataInspecao);
    if (ano) {
      acc[ano] = (acc[ano] || 0) + 1;
    }
    return acc;
  }, {});

  console.log(`\nDepartamentos encontrados: ${departments.length}`);
  console.log(`Departamentos: ${departments.join(', ')}`);
  console.log('\nCriticidades:');
  Object.entries(criticidades).forEach(([key, value]) => {
    console.log(`  ${key}: ${value} inspe√ß√µes`);
  });
  console.log('\nInspe√ß√µes por Ano:');
  Object.entries(anoStats).sort().forEach(([ano, count]) => {
    console.log(`  ${ano}: ${count} inspe√ß√µes`);
  });

  return inspections;
}

// Executar importa√ß√£o
try {
  if (!fs.existsSync('data')) {
    fs.mkdirSync('data');
  }

  const inspections = processExcelData();
  console.log('\n‚úÖ Importa√ß√£o conclu√≠da com sucesso!');
} catch (error) {
  console.error('‚ùå Erro durante a importa√ß√£o:', error.message);
}

scripts/load-json-data.js.content:
import fs from 'fs';
import { Pool } from '@neondatabase/serverless';
import ws from 'ws';

// Configure WebSocket for serverless
const neonConfig = { webSocketConstructor: ws };

async function loadRealData() {
  console.log('Carregando dados reais do arquivo JSON...');

  try {
    // Read the JSON file
    const jsonData = fs.readFileSync('../data/real-inspections.json', 'utf8');
    const inspections = JSON.parse(jsonData);

    console.log(`Total de registros encontrados: ${inspections.length}`);

    // Connect to database
    const pool = new Pool({ connectionString: process.env.DATABASE_URL });

    // Clear existing data
    await pool.query('DELETE FROM inspections');
    console.log('Dados anteriores removidos');

    // Insert data in batches
    const batchSize = 500;
    let totalInserted = 0;

    for (let i = 0; i < inspections.length; i += batchSize) {
      const batch = inspections.slice(i, i + batchSize);

      const values = batch.map(inspection => [
        inspection.numero,
        inspection.unidadeInspecionada,
        inspection.departamento,
        inspection.coorpin,
        inspection.dataInspecao,
        inspection.delegadoCorregedor,
        inspection.naoConformidade,
        inspection.descricaoNaoConformidade,
        inspection.providenciasIniciais,
        inspection.providenciasIntermediarias,
        inspection.providenciasConclusivas,
        inspection.dataInicioRegularizacao,
        inspection.diasPrazo,
        inspection.dataFimRegularizacao,
        inspection.statusPrazo,
        inspection.dataNovaInspecao,
        inspection.criticidade
      ]);

      const placeholders = values.map((_, index) => 
        `($${index * 17 + 1}, $${index * 17 + 2}, $${index * 17 + 3}, $${index * 17 + 4}, $${index * 17 + 5}, $${index * 17 + 6}, $${index * 17 + 7}, $${index * 17 + 8}, $${index * 17 + 9}, $${index * 17 + 10}, $${index * 17 + 11}, $${index * 17 + 12}, $${index * 17 + 13}, $${index * 17 + 14}, $${index * 17 + 15}, $${index * 17 + 16}, $${index * 17 + 17})`
      ).join(', ');

      const flatValues = values.flat();

      const query = `
        INSERT INTO inspections (
          numero, unidade_inspecionada, departamento, coorpin, data_inspecao,
          delegado_corregedor, nao_conformidade, descricao_nao_conformidade,
          providencias_iniciais, providencias_intermediarias, providencias_conclusivas,
          data_inicio_prazo_regularizacao, dias_prazo, data_fim_regularizacao,
          status_prazo, data_determinada_nova_inspecao, criticidade
        ) VALUES ${placeholders}
      `;

      await pool.query(query, flatValues);
      totalInserted += batch.length;

      console.log(`Processados ${totalInserted}/${inspections.length} registros`);
    }

    // Verify data
    const result = await pool.query(`
      SELECT 
        COUNT(*) as total_records,
        COUNT(DISTINCT CONCAT(unidade_inspecionada, '-', data_inspecao)) as unique_inspections,
        EXTRACT(YEAR FROM data_inspecao) as ano,
        COUNT(*) as quantidade
      FROM inspections 
      WHERE data_inspecao IS NOT NULL
      GROUP BY EXTRACT(YEAR FROM data_inspecao)
      ORDER BY ano
    `);

    console.log('\n‚úÖ Dados carregados com sucesso!');
    console.log(`Total de registros: ${totalInserted}`);
    console.log('Distribui√ß√£o por ano:', result.rows);

    await pool.end();

  } catch (error) {
    console.error('‚ùå Erro durante o carregamento:', error.message);
    process.exit(1);
  }
}

loadRealData();

scripts/load-via-api.js.content:
import fs from 'fs';

async function loadDataViaAPI() {
  console.log('Carregando dados via API...');

  try {
    // Read the JSON file
    const jsonData = fs.readFileSync('../data/real-inspections.json', 'utf8');
    const inspections = JSON.parse(jsonData);

    console.log(`Total de registros encontrados: ${inspections.length}`);

    // Send data in batches via API
    const batchSize = 100;
    let totalLoaded = 0;

    for (let i = 0; i < inspections.length; i += batchSize) {
      const batch = inspections.slice(i, i + batchSize);

      try {
        const response = await fetch('http://localhost:5000/api/inspections/bulk', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ inspections: batch })
        });

        if (response.ok) {
          totalLoaded += batch.length;
          console.log(`Carregados ${totalLoaded}/${inspections.length} registros`);
        } else {
          console.error(`Erro no batch ${i}: ${response.status}`);
        }
      } catch (error) {
        console.error(`Erro ao enviar batch ${i}:`, error.message);
      }
    }

    console.log(`\n‚úÖ Carregamento conclu√≠do: ${totalLoaded} registros`);

  } catch (error) {
    console.error('‚ùå Erro durante o carregamento:', error.message);
    process.exit(1);
  }
}

loadDataViaAPI();

