import type { Express, Request, Response, NextFunction } from "express";

// Estender Request para incluir user
declare global {
  namespace Express {
    interface Request {
      user?: any;
    }
  }
}
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { loginSchema, insertInspectionSchema, insertControlePrazoSchema } from "@shared/schema";
import Papa from 'papaparse';
import fs from 'fs';
import path from 'path';
import multer from 'multer';
import * as XLSX from 'xlsx';

// Middleware para verificar autentica√ß√£o
const requireAuth = async (req: Request, res: Response, next: NextFunction) => {
  try {
    const authHeader = req.headers.authorization;
    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return res.status(401).json({ success: false, message: 'Token de acesso requerido' });
    }

    const token = authHeader.substring(7);
    // Verificar se o token √© v√°lido (username:password base64)
    const decoded = Buffer.from(token, 'base64').toString('utf-8');
    const [username, password] = decoded.split(':');

    const user = await storage.getUserByUsername(username);
    if (!user || user.password !== password) {
      return res.status(401).json({ success: false, message: 'Credenciais inv√°lidas' });
    }

    req.user = user;
    next();
  } catch (error) {
    res.status(401).json({ success: false, message: 'Token inv√°lido' });
  }
};

// Configura√ß√£o do multer para upload de arquivos
const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 50 * 1024 * 1024 // 50MB limit
  },
  fileFilter: (req, file, cb) => {
    if (file.mimetype === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' ||
        file.mimetype === 'application/vnd.ms-excel') {
      cb(null, true);
    } else {
      cb(new Error('Apenas arquivos Excel (.xlsx, .xls) s√£o permitidos'));
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication endpoint
  app.post('/api/login', async (req, res) => {
    try {
      const { username, password } = loginSchema.parse(req.body);

      const user = await storage.getUserByUsername(username);
      if (user && user.password === password) {
        // Gerar token de acesso para uploads protegidos
        const token = Buffer.from(`${username}:${password}`).toString('base64');
        res.json({ 
          success: true, 
          user: { id: user.id, username: user.username },
          accessToken: token
        });
      } else {
        res.status(401).json({ success: false, message: 'Credenciais inv√°lidas' });
      }
    } catch (error) {
      res.status(400).json({ success: false, message: 'Dados inv√°lidos' });
    }
  });

  // Get all inspections
  app.get('/api/inspections', async (req, res) => {
    try {
      const inspections = await storage.getAllInspections();
      res.json(inspections);
    } catch (error) {
      res.status(500).json({ error: 'Erro ao buscar inspe√ß√µes' });
    }
  });

  // Autentica√ß√£o administrativa adicional
  app.post('/api/admin/authenticate', requireAuth, async (req, res) => {
    try {
      const { adminPassword } = req.body;

      if (!adminPassword) {
        return res.status(400).json({ success: false, message: 'Senha administrativa √© obrigat√≥ria' });
      }

      // Senha administrativa espec√≠fica (mais segura que as credenciais principais)
      const ADMIN_PASSWORD = 'ASTEC@Admin#2025!BA';

      if (adminPassword === ADMIN_PASSWORD) {
        console.log(`‚úÖ Acesso administrativo concedido para ${req.user.username}`);
        res.json({ 
          success: true, 
          message: 'Acesso administrativo concedido',
          user: req.user.username 
        });
      } else {
        console.log(`‚ùå Tentativa de acesso administrativo negada para ${req.user.username}`);
        res.status(401).json({ success: false, message: 'Senha administrativa incorreta' });
      }
    } catch (error) {
      res.status(500).json({ success: false, message: 'Erro interno do servidor' });
    }
  });

  // Upload de planilha Excel protegido por autentica√ß√£o
  app.post('/api/admin/upload-excel', requireAuth, upload.single('excel'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: 'Arquivo Excel √© obrigat√≥rio' });
      }

      // Processar arquivo Excel
      const workbook = XLSX.read(req.file.buffer, { type: 'buffer' });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];

      // Converter para JSON
      const rawData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      if (rawData.length < 2) {
        return res.status(400).json({ error: 'Planilha deve conter pelo menos um cabe√ßalho e uma linha de dados' });
      }

      // Processar dados da planilha
      const headers = rawData[0] as string[];
      const processedData = [];

      for (let i = 1; i < rawData.length; i++) {
        const row = rawData[i] as any[];
        const inspection: any = {};

        headers.forEach((header, index) => {
          const value = row[index];

          // Mapear colunas da planilha para campos do banco
          switch (header) {
            case 'N¬∫':
            case 'Numero':
              inspection.numero = value?.toString() || '';
              break;
            case 'Unidade Inspecionada':
              inspection.unidadeInspecionada = value?.toString() || '';
              break;
            case 'Departamento':
              inspection.departamento = value?.toString() || '';
              break;
            case 'COORPIN':
              inspection.coorpin = value?.toString() || '';
              break;
            case 'Data da Inspe√ß√£o':
              if (typeof value === 'number') {
                // Converter data serial do Excel
                const date = new Date((value - 25569) * 86400 * 1000);
                inspection.dataInspecao = date.toISOString().split('T')[0];
              } else if (value) {
                inspection.dataInspecao = value.toString();
              }
              break;
            case 'Delegado Corregedor Respons√°vel':
              inspection.delegadoCorregedor = value?.toString() || '';
              break;
            case 'N√£o Conformidade Identificada':
              inspection.naoConformidade = value?.toString() || '';
              break;
            case 'Descri√ß√£o da N√£o Conformidade Identificada':
              inspection.descricaoNaoConformidade = value?.toString() || '';
              break;
            case 'Provid√™ncias Iniciais':
              inspection.providenciasIniciais = value?.toString() || '';
              break;
            case 'Provid√™ncias Intermedi√°rias':
              inspection.providenciasIntermediarias = value?.toString() || '';
              break;
            case 'Provid√™ncias Conclusivas':
              inspection.providenciasConclusivas = value?.toString() || '';
              break;
            case 'Data In√≠cio Prazo Regulariza√ß√£o':
              if (typeof value === 'number') {
                const date = new Date((value - 25569) * 86400 * 1000);
                inspection.dataInicioPrazo = date.toISOString().split('T')[0];
              } else if (value) {
                inspection.dataInicioPrazo = value.toString();
              }
              break;
            case 'Dias Prazo':
              inspection.diasPrazo = typeof value === 'number' ? value : parseInt(value) || 0;
              break;
            case 'Data Fim para Regulariza√ß√£o':
              if (typeof value === 'number') {
                const date = new Date((value - 25569) * 86400 * 1000);
                inspection.dataFimRegularizacao = date.toISOString().split('T')[0];
              } else if (value) {
                inspection.dataFimRegularizacao = value.toString();
              }
              break;
            case 'Status Prazo para Regulariza√ß√£o':
              inspection.statusPrazo = value?.toString() || '';
              break;
            case 'Data Determinada para Nova Inspe√ß√£o':
              if (typeof value === 'number') {
                const date = new Date((value - 25569) * 86400 * 1000);
                inspection.dataDeterminadaNovaInspecao = date.toISOString().split('T')[0];
              } else if (value) {
                inspection.dataDeterminadaNovaInspecao = value.toString();
              }
              break;
            case 'Criticidade':
              inspection.criticidade = value?.toString() || '';
              break;
          }
        });

        // Validar dados m√≠nimos
        if (inspection.unidadeInspecionada && inspection.departamento) {
          processedData.push(inspection);
        }
      }

      if (processedData.length === 0) {
        return res.status(400).json({ error: 'Nenhum dado v√°lido encontrado na planilha' });
      }

      // Salvar dados no banco - APENAS INSPE√á√ïES, NUNCA CONTROLE DE PRAZOS
      await storage.bulkCreateInspections(processedData);

      console.log(`‚úÖ UPLOAD DE INSPE√á√ïES: Administrador ${req.user.username} carregou ${processedData.length} registros via Excel`);
      console.log(`üîí SEPARA√á√ÉO DE DADOS: Este upload alimenta APENAS Dashboard/Relat√≥rios, N√ÉO Status Regulariza√ß√£o`);

      res.json({
        success: true,
        message: `${processedData.length} registros processados e carregados com sucesso`,
        count: processedData.length,
        uploadedBy: req.user.username,
        timestamp: new Date().toISOString()
      });

    } catch (error) {
      console.error('Erro no upload de Excel:', error);
      res.status(500).json({ error: 'Erro ao processar planilha Excel' });
    }
  });

  // Bulk insert inspections
  app.post('/api/inspections/bulk', async (req, res) => {
    try {
      const { inspections } = req.body;
      if (!inspections || !Array.isArray(inspections)) {
        return res.status(400).json({ error: 'Array de inspe√ß√µes requerido' });
      }

      const validInspections = inspections.map(inspection => ({
        numero: inspection.numero || '001',
        unidadeInspecionada: inspection.unidadeInspecionada || '',
        departamento: inspection.departamento || '',
        coorpin: inspection.coorpin || '',
        dataInspecao: inspection.dataInspecao,
        delegadoCorregedor: inspection.delegadoCorregedor || '',
        naoConformidade: inspection.naoConformidade || '',
        descricaoNaoConformidade: inspection.descricaoNaoConformidade || '',
        providenciasIniciais: inspection.providenciasIniciais || '',
        providenciasIntermediarias: inspection.providenciasIntermediarias || '',
        providenciasConclusivas: inspection.providenciasConclusivas || '',
        dataInicioPrazo: inspection.dataInicioRegularizacao,
        diasPrazo: inspection.diasPrazo || 0,
        dataFimRegularizacao: inspection.dataFimRegularizacao,
        statusPrazo: inspection.statusPrazo || '',
        dataDeterminadaNovaInspecao: inspection.dataNovaInspecao,
        criticidade: inspection.criticidade || ''
      }));

      await storage.bulkCreateInspections(validInspections);
      res.json({ 
        success: true, 
        message: `${validInspections.length} inspe√ß√µes carregadas`,
        count: validInspections.length 
      });
    } catch (error) {
      console.error('Erro no bulk insert:', error);
      res.status(500).json({ error: 'Erro ao carregar inspe√ß√µes em lote' });
    }
  });

  // Get filtered inspections
  app.get('/api/inspections/filtered', async (req, res) => {
    try {
      const filters = req.query;
      const inspections = await storage.getInspectionsByFilters(filters);
      res.json(inspections);
    } catch (error) {
      res.status(500).json({ error: 'Erro ao filtrar inspe√ß√µes' });
    }
  });

  // Load CSV data
  app.post('/api/inspections/load-csv', async (req, res) => {
    try {
      const csvFilePath = path.join(process.cwd(), 'data', 'inspections.csv');

      if (!fs.existsSync(csvFilePath)) {
        return res.status(404).json({ error: 'Arquivo CSV n√£o encontrado' });
      }

      const csvData = fs.readFileSync(csvFilePath, 'utf8');

      Papa.parse(csvData, {
        header: true,
        skipEmptyLines: true,
        transformHeader: (header: string) => {
          // Transform CSV headers to match schema
          const headerMap: { [key: string]: string } = {
            'N¬∫': 'numero',
            'Unidade Inspecionada': 'unidadeInspecionada',
            'Departamento': 'departamento',
            'COORPIN': 'coorpin',
            'Data da Inspe√ß√£o': 'dataInspecao',
            'Delegado Corregedor Respons√°vel': 'delegadoCorregedor',
            'N√£o Conformidade Identificada': 'naoConformidade',
            'Descri√ß√£o da N√£o Conformidade Identificada': 'descricaoNaoConformidade',
            'Provid√™ncias Iniciais': 'providenciasIniciais',
            'Provid√™ncias Intermedi√°rias': 'providenciasIntermediarias',
            'Provid√™ncias Conclusivas': 'providenciasConclusivas',
            'Data In√≠cio Prazo Regulariza√ß√£o': 'dataInicioPrazo',
            'Dias Prazo': 'diasPrazo',
            'Data Fim para Regulariza√ß√£o': 'dataFimRegularizacao',
            'Status Prazo para Regulariza√ß√£o': 'statusPrazo',
            'Data Determinada para Nova Inspe√ß√£o': 'dataDeterminadaNovaInspecao',
            'Criticidade': 'criticidade'
          };
          return headerMap[header] || header;
        },
        transform: (value: string, field: string) => {
          // Handle date conversions and Excel serial dates
          if (field === 'dataInspecao' || field === 'dataInicioPrazo' || 
              field === 'dataFimRegularizacao' || field === 'dataDeterminadaNovaInspecao') {
            if (value && !isNaN(Number(value))) {
              // Convert Excel serial date to JavaScript Date
              const excelDate = new Date((Number(value) - 25569) * 86400 * 1000);
              return excelDate.toISOString().split('T')[0];
            }
          }
          if (field === 'diasPrazo') {
            return value ? parseInt(value) : null;
          }
          return value || null;
        },
        complete: async (results: any) => {
          try {
            const validInspections = results.data
              .filter((row: any) => row.numero && row.unidadeInspecionada)
              .map((row: any) => {
                // Validate and create inspection object
                return {
                  numero: row.numero,
                  unidadeInspecionada: row.unidadeInspecionada,
                  departamento: row.departamento || '',
                  coorpin: row.coorpin || '',
                  dataInspecao: row.dataInspecao,
                  delegadoCorregedor: row.delegadoCorregedor || '',
                  naoConformidade: row.naoConformidade || '',
                  descricaoNaoConformidade: row.descricaoNaoConformidade || '',
                  providenciasIniciais: row.providenciasIniciais,
                  providenciasIntermediarias: row.providenciasIntermediarias,
                  providenciasConclusivas: row.providenciasConclusivas,
                  dataInicioPrazo: row.dataInicioPrazo,
                  diasPrazo: row.diasPrazo,
                  dataFimRegularizacao: row.dataFimRegularizacao,
                  statusPrazo: row.statusPrazo || '',
                  dataDeterminadaNovaInspecao: row.dataDeterminadaNovaInspecao,
                  criticidade: row.criticidade || ''
                };
              });

            await storage.bulkCreateInspections(validInspections);
            res.json({ 
              success: true, 
              message: `${validInspections.length} inspe√ß√µes carregadas com sucesso`,
              count: validInspections.length 
            });
          } catch (error) {
            res.status(500).json({ error: 'Erro ao processar dados CSV' });
          }
        },
        error: (error: any) => {
          res.status(400).json({ error: 'Erro ao processar arquivo CSV' });
        }
      });
    } catch (error) {
      res.status(500).json({ error: 'Erro interno do servidor' });
    }
  });

  // Generate report data
  app.get('/api/reports/data', async (req, res) => {
    try {
      console.log('Filters received:', req.query);

      // Convert query parameters properly
      const filters: any = {};

      if (req.query.ano) {
        filters.ano = parseInt(req.query.ano as string);
      }

      if (req.query.departamento) {
        filters.departamento = [req.query.departamento as string];
      }

      if (req.query.unidade) {
        filters.unidade = [req.query.unidade as string];
      }

      if (req.query.dataInicial) {
        filters.dataInicial = req.query.dataInicial as string;
      }

      if (req.query.dataFinal) {
        filters.dataFinal = req.query.dataFinal as string;
      }

      if (req.query.statusPrazo) {
        filters.statusPrazo = req.query.statusPrazo as string;
      }


      console.log('Processed filters:', filters);

      const inspections = await storage.getInspectionsByFilters(filters);
      console.log(`Found ${inspections.length} inspections`);

      // Buscar dados de controle de prazos para calcular "Com Prazo Definido"
      const allControlePrazos = await storage.getAllControlePrazos();

      // Filtrar controle de prazos baseado nas unidades das inspe√ß√µes filtradas
      const filteredUnidades = new Set(inspections.map(i => i.unidadeInspecionada));
      const controlePrazos = allControlePrazos.filter(cp => 
        filteredUnidades.size === 0 || filteredUnidades.has(cp.unidade)
      );

      console.log(`Found ${controlePrazos.length} controle de prazos records (filtered from ${allControlePrazos.length})`);

      // Count unique inspections by unit, date and inspector (more accurate)
      const uniqueInspections = new Set();
      inspections.forEach(inspection => {
        const key = `${inspection.unidadeInspecionada}-${inspection.dataInspecao}-${inspection.delegadoCorregedor}`;
        uniqueInspections.add(key);
      });

      // Calculate status counts for unique inspections
      const statusCounts = { withinDeadline: 0, nearDeadline: 0, overdue: 0 };
      const inspectionStatusMap = new Map();

      inspections.forEach(inspection => {
        const key = `${inspection.unidadeInspecionada}-${inspection.dataInspecao}-${inspection.delegadoCorregedor}`;
        if (!inspectionStatusMap.has(key)) {
          inspectionStatusMap.set(key, inspection.statusPrazo);
          if (inspection.statusPrazo === 'Dentro do prazo') statusCounts.withinDeadline++;
          else if (inspection.statusPrazo === 'Pr√≥ximo do vencimento') statusCounts.nearDeadline++;
          else if (inspection.statusPrazo === 'Prazo expirado') statusCounts.overdue++;
        }
      });

      // Calcular unidades com prazo definido baseado nos dados de controle de prazos
      const unidadesComPrazoDefinido = new Set(controlePrazos.map(cp => cp.unidade)).size;

      // Calcular unidades com prazo vencido (data prazo menor que hoje)
      const hoje = new Date();
      hoje.setHours(0, 0, 0, 0); // Zerar hor√°rios para compara√ß√£o apenas de datas

      const unidadesComPrazoVencido = new Set(
        controlePrazos
          .filter(cp => {
            if (!cp.dataPrazo) return false;
            const dataPrazo = new Date(cp.dataPrazo);
            dataPrazo.setHours(0, 0, 0, 0);
            return dataPrazo < hoje;
          })
          .map(cp => cp.unidade)
      ).size;

      // Calcular dados espec√≠ficos do Status de Regulariza√ß√£o
      const regularizados = controlePrazos.filter(cp => cp.status === 'regularizado').length;
      const pendentes = controlePrazos.filter(cp => cp.status === 'pendente').length;
      const naoRegularizados = controlePrazos.filter(cp => cp.status === 'nao_regularizado').length;

      // Process data for reports
      const reportData = {
        inspections,
        summary: {
          totalInspections: uniqueInspections.size, // Unique inspections by unit, date, inspector
          totalNonCompliances: inspections.length, // Total non-conformities
          withinDeadline: unidadesComPrazoDefinido, // Unidades com prazo definido
          nearDeadline: unidadesComPrazoVencido, // Unidades com prazo vencido
          overdue: unidadesComPrazoVencido, // Tamb√©m usar para overdue
          // Dados espec√≠ficos do Status de Regulariza√ß√£o
          regularizados: regularizados,
          pendentes: pendentes,
          naoRegularizados: naoRegularizados,
          totalControlePrazos: controlePrazos.length
        },
        charts: {
          departmentData: inspections.reduce((acc: any[], item) => {
            const key = `${item.unidadeInspecionada}-${item.dataInspecao}-${item.delegadoCorregedor}`;
            const dept = acc.find(d => d.name === item.departamento);
            if (dept) {
              if (!dept.uniqueInspections) dept.uniqueInspections = new Set();
              if (!dept.uniqueInspections.has(key)) {
                dept.uniqueInspections.add(key);
                dept.value += 1; // Count unique inspections only
              }
            } else {
              const uniqueInspections = new Set([key]);
              acc.push({ name: item.departamento, value: 1, uniqueInspections });
            }
            return acc;
          }, []).map((item: any) => ({ name: item.name, value: item.value })),
          statusData: controlePrazos.reduce((acc: any[], item) => {
            // Categorizar baseado na situa√ß√£o do prazo
            let statusName = '';

            if (!item.dataPrazo) {
              statusName = 'Sem Prazo Definido';
            } else {
              const dataPrazo = new Date(item.dataPrazo);
              dataPrazo.setHours(0, 0, 0, 0);
              const hoje = new Date();
              hoje.setHours(0, 0, 0, 0);

              if (dataPrazo < hoje) {
                statusName = 'Com Prazo Vencido';
              } else {
                // Verificar se est√° pr√≥ximo do vencimento (pr√≥ximos 15 dias)
                const diasRestantes = Math.ceil((dataPrazo.getTime() - hoje.getTime()) / (1000 * 3600 * 24));
                if (diasRestantes <= 15) {
                  statusName = 'Pr√≥ximo do Vencimento';
                } else {
                  statusName = 'Com Prazo Definido';
                }
              }
            }

            const status = acc.find(s => s.name === statusName);
            if (status) {
              status.value += 1;
            } else {
              acc.push({ name: statusName, value: 1 });
            }
            return acc;
          }, []),
          nonComplianceData: inspections.reduce((acc: any[], item) => {
            const compliance = acc.find(c => c.name === item.naoConformidade);
            if (compliance) {
              compliance.value += 1;
            } else {
              acc.push({ name: item.naoConformidade, value: 1 });
            }
            return acc;
          }, [])
        }
      };

      res.json(reportData);
    } catch (error) {
      console.error('Error in /api/reports/data:', error);
      res.status(500).json({ error: 'Erro ao gerar dados do relat√≥rio', details: error });
    }
  });

  // Upload endpoint for controle de prazos
  app.post('/api/admin/upload-controle-prazos', requireAuth, upload.single('excel'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: 'Nenhum arquivo foi enviado' });
      }

      console.log(`üì§ Admin upload de controle de prazos iniciado pelo usu√°rio: ${req.user.username}`);
      console.log(`üìÑ Arquivo: ${req.file.originalname} (${req.file.size} bytes)`);

      // Ler arquivo Excel
      const workbook = XLSX.read(req.file.buffer, { type: 'buffer' });
      const firstSheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[firstSheetName];

      // Converter para JSON
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
      console.log(`üìä Total de linhas no Excel: ${jsonData.length}`);

      if (jsonData.length < 2) {
        return res.status(400).json({ error: 'Arquivo Excel deve conter pelo menos uma linha de cabe√ßalho e uma linha de dados' });
      }

      // Primeira linha s√£o os cabe√ßalhos
      const headers = jsonData[0] as string[];
      const dataRows = jsonData.slice(1);

      console.log('üìã Cabe√ßalhos encontrados:', headers);

      // Mapeamento de colunas (flex√≠vel para aceitar varia√ß√µes de nomes)
      const findColumnIndex = (possibleNames: string[]): number => {
        for (const name of possibleNames) {
          const index = headers.findIndex(h => 
            h && h.toString().toLowerCase().trim().includes(name.toLowerCase())
          );
          if (index !== -1) return index;
        }
        return -1;
      };

      const columnMapping = {
        unidade: findColumnIndex(['unidade', 'unit']),
        oficio: findColumnIndex(['of√≠cio', 'oficio', 'documento', 'doc']),
        linkSei: findColumnIndex(['link sei', 'sei', 'link']),
        naoConformidade: findColumnIndex(['n√£o conformidade', 'nao conformidade', 'conformidade']),
        dataRecebimento: findColumnIndex(['data recebimento', 'recebimento', 'data rec']),
        dataPrazo: findColumnIndex(['data prazo', 'prazo', 'vencimento']),
        status: findColumnIndex(['status', 'situa√ß√£o', 'situacao', 'regularizado'])
      };

      console.log('üóÇÔ∏è Mapeamento de colunas:', columnMapping);

      // Verificar se colunas obrigat√≥rias foram encontradas
      const requiredColumns = ['unidade', 'oficio', 'naoConformidade', 'dataRecebimento', 'dataPrazo', 'status'] as const;
      const missingColumns = requiredColumns.filter(col => columnMapping[col] === -1);

      if (missingColumns.length > 0) {
        return res.status(400).json({ 
          error: `Colunas obrigat√≥rias n√£o encontradas: ${missingColumns.join(', ')}`,
          found: headers,
          required: requiredColumns
        });
      }

      const parseExcelDate = (value: any): Date | null => {
        if (!value) return null;

        // Se √© um n√∫mero (data serial do Excel)
        if (typeof value === 'number') {
          return new Date((value - 25569) * 86400 * 1000);
        }

        // Se √© string, tentar parsear
        if (typeof value === 'string') {
          // Tentar formato DD/MM/AAAA
          const ddmmyyyy = value.match(/^(\d{1,2})\/(\d{1,2})\/(\d{4})$/);
          if (ddmmyyyy) {
            return new Date(parseInt(ddmmyyyy[3]), parseInt(ddmmyyyy[2]) - 1, parseInt(ddmmyyyy[1]));
          }

          // Tentar outros formatos
          const parsed = new Date(value);
          if (!isNaN(parsed.getTime())) {
            return parsed;
          }
        }

        return null;
      };

      // Fun√ß√£o para normalizar status
      const normalizeStatus = (value: any): string => {
        if (!value) return 'pendente';
        const str = value.toString().toLowerCase().trim();

        if (str.includes('regular')) return 'regularizado';
        if (str.includes('n√£o regular') || str.includes('nao regular')) return 'nao_regularizado';
        if (str.includes('pendent')) return 'pendente';

        return 'pendente';
      };

      // Processar dados
      const controlePrazosData = [];
      let processedCount = 0;
      let errorCount = 0;

      for (let i = 0; i < dataRows.length; i++) {
        const row = dataRows[i] as any[];

        try {
          const unidade = row[columnMapping.unidade]?.toString()?.trim();
          const oficio = row[columnMapping.oficio]?.toString()?.trim();
          const naoConformidade = row[columnMapping.naoConformidade]?.toString()?.trim();

          if (!unidade || !oficio || !naoConformidade) {
            console.log(`‚ö†Ô∏è Linha ${i + 2}: Dados obrigat√≥rios em branco`);
            errorCount++;
            continue;
          }

          const dataRecebimento = parseExcelDate(row[columnMapping.dataRecebimento]);
          const dataPrazo = parseExcelDate(row[columnMapping.dataPrazo]);

          if (!dataRecebimento || !dataPrazo) {
            console.log(`‚ö†Ô∏è Linha ${i + 2}: Datas inv√°lidas`);
            errorCount++;
            continue;
          }

          const controlePrazoData = {
            unidade,
            oficio,
            linkSei: row[columnMapping.linkSei]?.toString()?.trim() || null,
            naoConformidade,
            dataRecebimento: dataRecebimento.toISOString().split('T')[0],
            dataPrazo: dataPrazo.toISOString().split('T')[0],
            status: normalizeStatus(row[columnMapping.status]),
            observacoes: null
          };

          controlePrazosData.push(controlePrazoData);
          processedCount++;

        } catch (error) {
          console.error(`‚ùå Erro na linha ${i + 2}:`, error);
          errorCount++;
        }
      }

      console.log(`‚úÖ Dados processados: ${processedCount}, Erros: ${errorCount}`);

      if (controlePrazosData.length === 0) {
        return res.status(400).json({ 
          error: 'Nenhum registro v√°lido foi encontrado na planilha',
          details: `${errorCount} linhas com erro`
        });
      }

      // Inserir dados com deduplica√ß√£o autom√°tica - UPLOAD EXPL√çCITO DE CONTROLE DE PRAZOS
      console.log(`üîí CONTROLE DE PRAZOS: Upload expl√≠cito iniciado pelo administrador ${req.user.username}`);
      await storage.insertControlePrazosInBatch(controlePrazosData);
      console.log(`‚úÖ SEPARA√á√ÉO DE DADOS: Este upload alimenta APENAS Status Regulariza√ß√£o, N√ÉO Dashboard/Relat√≥rios`);

      const result = {
        success: true,
        count: controlePrazosData.length,
        errors: errorCount,
        uploadedBy: req.user.username,
        timestamp: new Date().toISOString(),
        message: `${controlePrazosData.length} registros de controle de prazos carregados com sucesso. ${errorCount > 0 ? `${errorCount} linhas com erro foram ignoradas.` : ''}`
      };

      console.log('‚úÖ Upload de controle de prazos conclu√≠do:', result);
      res.json(result);

    } catch (error) {
      console.error('‚ùå Erro no upload de controle de prazos:', error);
      console.error('Stack trace:', error instanceof Error ? error.stack : error);
      res.status(500).json({ 
        error: 'Erro interno do servidor durante o upload',
        details: error instanceof Error ? error.message : 'Erro desconhecido'
      });
    }
  });

  // Admin delete endpoints
  app.delete('/api/admin/delete-all-inspections', requireAuth, async (req, res) => {
    try {
      await storage.deleteAllInspections();

      const result = {
        success: true,
        message: 'Todos os dados de inspe√ß√µes foram removidos',
        deletedBy: req.user.username,
        timestamp: new Date().toISOString()
      };

      console.log('üóëÔ∏è Todos os dados de inspe√ß√µes removidos por:', req.user.username);
      res.json(result);
    } catch (error) {
      console.error('Erro ao remover dados de inspe√ß√µes:', error);
      res.status(500).json({ 
        error: 'Erro interno do servidor', 
        details: error instanceof Error ? error.message : 'Erro desconhecido'
      });
    }
  });

  app.delete('/api/admin/delete-all-controle-prazos', requireAuth, async (req, res) => {
    try {
      await storage.deleteAllControlePrazos();

      const result = {
        success: true,
        message: 'Todos os dados de controle de prazos foram removidos',
        deletedBy: req.user.username,
        timestamp: new Date().toISOString()
      };

      console.log('üóëÔ∏è Todos os dados de controle de prazos removidos por:', req.user.username);
      res.json(result);
    } catch (error) {
      console.error('Erro ao remover dados de controle de prazos:', error);
      res.status(500).json({ 
        error: 'Erro interno do servidor', 
        details: error instanceof Error ? error.message : 'Erro desconhecido'
      });
    }
  });

  // Controle de Prazos routes
  app.get('/api/controle-prazos', async (req, res) => {
    try {
      const controlePrazos = await storage.getAllControlePrazos();
      res.json(controlePrazos);
    } catch (error) {
      console.error('Erro ao buscar controle de prazos:', error);
      res.status(500).json({ error: 'Erro ao buscar registros de controle de prazos' });
    }
  });

  app.post('/api/controle-prazos', async (req, res) => {
    try {
      const validData = insertControlePrazoSchema.parse(req.body);
      const controlePrazo = await storage.createControlePrazo(validData);
      res.json(controlePrazo);
    } catch (error) {
      console.error('Erro ao criar controle de prazo:', error);
      res.status(500).json({ error: 'Erro ao criar registro de controle de prazo' });
    }
  });

  app.patch('/api/controle-prazos/:id', async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const updateData = req.body;

      if (isNaN(id)) {
        return res.status(400).json({ error: 'ID inv√°lido' });
      }

      console.log(`Updating controle-prazo ${id} with data:`, updateData);

      const controlePrazo = await storage.updateControlePrazo(id, updateData);

      if (!controlePrazo) {
        return res.status(404).json({ error: 'Registro n√£o encontrado' });
      }

      res.json(controlePrazo);
    } catch (error) {
      console.error('Erro ao atualizar controle de prazo:', error);
      res.status(500).json({ error: 'Erro ao atualizar registro de controle de prazo' });
    }
  });

  app.delete('/api/controle-prazos/:id', async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteControlePrazo(id);
      res.json({ success: true });
    } catch (error) {
      console.error('Erro ao deletar controle de prazo:', error);
      res.status(500).json({ error: 'Erro ao deletar registro de controle de prazo' });
    }
  });

  // Gallery routes
  app.get('/api/gallery/photos', async (req, res) => {
    try {
      const photos = await storage.getAllPhotos();
      res.json(photos);
    } catch (error) {
      console.error('Erro ao buscar fotos da galeria:', error);
      res.status(500).json({ error: 'Erro ao buscar fotos da galeria' });
    }
  });

  app.get('/api/gallery/photos/unidade/:unidade', async (req, res) => {
    try {
      const { unidade } = req.params;
      const photos = await storage.getPhotosByUnidade(decodeURIComponent(unidade));
      res.json(photos);
    } catch (error) {
      console.error('Erro ao buscar fotos por unidade:', error);
      res.status(500).json({ error: 'Erro ao buscar fotos da unidade' });
    }
  });

  app.post('/api/gallery/photos', async (req, res) => {
    try {
      const { unidade, fileName, fileType, fileSize, imageData } = req.body;

      if (!unidade || !fileName || !fileType || !imageData) {
        return res.status(400).json({ error: 'Dados obrigat√≥rios faltando' });
      }

      const photoData = {
        unidade,
        fileName,
        fileType,
        fileSize: fileSize || 0,
        imageData
      };

      const photo = await storage.createPhoto(photoData);
      res.json(photo);
    } catch (error) {
      console.error('Erro ao salvar foto na galeria:', error);
      res.status(500).json({ error: 'Erro ao salvar foto na galeria' });
    }
  });

  app.delete('/api/gallery/photos/:id', async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deletePhoto(id);
      res.json({ success: true });
    } catch (error) {
      console.error('Erro ao deletar foto da galeria:', error);
      res.status(500).json({ error: 'Erro ao deletar foto da galeria' });
    }
  });

  // Document routes
  app.get('/api/documents', async (req, res) => {
    try {
      const documents = await storage.getAllDocuments();
      res.json(documents);
    } catch (error) {
      console.error('Erro ao buscar documentos:', error);
      res.status(500).json({ error: 'Erro ao buscar documentos' });
    }
  });

  app.post('/api/documents', async (req, res) => {
    try {
      const { tipo, numero, ano, titulo, descricao, orgaoEmissor, dataPublicacao, dataVigencia, status, assunto, arquivoUrl } = req.body;

      if (!tipo || !numero || !ano || !titulo) {
        return res.status(400).json({ error: 'Tipo, n√∫mero, ano e t√≠tulo s√£o obrigat√≥rios' });
      }

      const documentData = {
        tipo,
        numero,
        ano,
        titulo,
        descricao: descricao || null,
        orgaoEmissor: orgaoEmissor || null,
        dataPublicacao: dataPublicacao || null,
        dataVigencia: dataVigencia || null,
        status: status || 'Vigente',
        assunto: assunto || null,
        arquivoUrl: arquivoUrl || null
      };

      const document = await storage.createDocument(documentData);
      res.json(document);
    } catch (error) {
      console.error('Erro ao criar documento:', error);
      res.status(500).json({ error: 'Erro ao criar documento' });
    }
  });

  app.put('/api/documents/:id', async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const updateData = req.body;

      const document = await storage.updateDocument(id, updateData);
      res.json(document);
    } catch (error) {
      console.error('Erro ao atualizar documento:', error);
      res.status(500).json({ error: 'Erro ao atualizar documento' });
    }
  });

  app.delete('/api/documents/:id', async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      await storage.deleteDocument(id);
      res.json({ success: true });
    } catch (error) {
      console.error('Erro ao deletar documento:', error);
      res.status(500).json({ error: 'Erro ao deletar documento' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { users, inspections, controlePrazos, galleryPhotos, documents, type User, type InsertUser, type Inspection, type InsertInspection, type ControlePrazo, type InsertControlePrazo, type GalleryPhoto, type InsertGalleryPhoto, type Document, type InsertDocument } from "@shared/schema";
import { db } from "./db";
import { eq, and, inArray, gte, lte, sql } from "drizzle-orm";

export interface IStorage {
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  getAllInspections(): Promise<Inspection[]>;
  getInspectionsByFilters(filters: any): Promise<Inspection[]>;
  createInspection(inspection: InsertInspection): Promise<Inspection>;
  bulkCreateInspections(inspections: InsertInspection[]): Promise<Inspection[]>;
  getAllControlePrazos(): Promise<ControlePrazo[]>;
  createControlePrazo(controlePrazo: InsertControlePrazo): Promise<ControlePrazo>;
  updateControlePrazo(id: number, controlePrazo: Partial<InsertControlePrazo>): Promise<ControlePrazo>;
  deleteControlePrazo(id: number): Promise<void>;
  clearControlePrazos(): Promise<void>;
  deleteAllInspections(): Promise<void>;
  deleteAllControlePrazos(): Promise<void>;
  insertControlePrazosInBatch(controlePrazosData: InsertControlePrazo[]): Promise<ControlePrazo[]>;
  bulkCreateControlePrazos(controlePrazosData: InsertControlePrazo[]): Promise<ControlePrazo[]>;
  // Gallery methods
  getPhotosByUnidade(unidade: string): Promise<GalleryPhoto[]>;
  getAllPhotos(): Promise<GalleryPhoto[]>;
  createPhoto(photo: InsertGalleryPhoto): Promise<GalleryPhoto>;
  deletePhoto(id: number): Promise<void>;
  // Document methods
  getAllDocuments(): Promise<Document[]>;
  createDocument(document: InsertDocument): Promise<Document>;
  updateDocument(id: number, document: Partial<InsertDocument>): Promise<Document>;
  deleteDocument(id: number): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  constructor() {
    // Initialize admin user if needed
    this.initializeAdminUser();
    // REMOVIDO: N√£o carregar dados de inspe√ß√£o automaticamente no startup
    // this.loadRealInspectionData();
  }

  private async initializeAdminUser() {
    const adminUser = await this.getUserByUsername('astec.admin');
    if (!adminUser) {
      await this.createUser({ username: 'astec.admin', password: 'Correpol@2025#BA' });
    }

    // Initialize some sample controle-prazos data if none exists
    // await this.initializeSampleControlePrazos(); // Comentado - n√£o criar dados de amostra
  }

  private async initializeSampleControlePrazos() {
    try {
      const existingRecords = await this.getAllControlePrazos();

      if (existingRecords.length === 0) {
        console.log('Creating sample controle-prazos data...');

        const sampleData = [
          {
            unidade: "1¬™ DRPC - Santo Ant√¥nio de Jesus",
            oficio: "OF-001/2025",
            linkSei: null,
            naoConformidade: "Aus√™ncia de livro de ocorr√™ncias atualizado",
            dataRecebimento: "2025-01-01",
            dataPrazo: "2025-02-01",
            status: "pendente" as const,
            observacoes: "Prazo inicial de 30 dias"
          },
          {
            unidade: "2¬™ DRPC - Feira de Santana",
            oficio: "OF-002/2025",
            linkSei: null,
            naoConformidade: "Falta de organiza√ß√£o do arquivo geral",
            dataRecebimento: "2025-01-05",
            dataPrazo: "2025-02-05",
            status: "regularizado" as const,
            observacoes: "Regularizado conforme of√≠cio de resposta"
          },
          {
            unidade: "3¬™ DRPC - Itabuna",
            oficio: "OF-003/2025",
            linkSei: null,
            naoConformidade: "Irregularidades no controle de armamento",
            dataRecebimento: "2025-01-10",
            dataPrazo: "2025-02-10",
            status: "nao_regularizado" as const,
            observacoes: "Pendente de documenta√ß√£o complementar"
          }
        ];

        for (const item of sampleData) {
          await this.createControlePrazo(item);
        }

        console.log('Sample controle-prazos data created successfully');
      }
    } catch (error) {
      console.error('Error initializing sample controle-prazos data:', error);
    }
  }

  private async loadRealInspectionData() {
    try {
      const fs = await import('fs');
      const path = await import('path');

      const dataPath = path.join(process.cwd(), 'data', 'real-inspections.json');

      if (fs.existsSync(dataPath)) {
        const existingInspections = await this.getAllInspections();

        // Only load if database is empty or has less than 100 records
        if (existingInspections.length < 100) {
          console.log('Loading real inspection data from Excel files...');

          const rawData = fs.readFileSync(dataPath, 'utf-8');
          const realData = JSON.parse(rawData);

          // Transform data to match our schema
          const inspectionsToInsert = realData.map((item: any) => ({
            numero: item.numero,
            unidadeInspecionada: item.unidadeInspecionada,
            departamento: item.departamento,
            coorpin: item.coorpin,
            dataInspecao: item.dataInspecao,
            delegadoCorregedor: item.delegadoCorregedor,
            naoConformidade: item.naoConformidade,
            descricaoNaoConformidade: item.descricaoNaoConformidade,
            providenciasIniciais: item.providenciasIniciais,
            providenciasIntermediarias: item.providenciasIntermediarias,
            providenciasConclusivas: item.providenciasConclusivas,
            dataInicioRegularizacao: item.dataInicioRegularizacao,
            diasPrazo: item.diasPrazo,
            dataFimRegularizacao: item.dataFimRegularizacao,
            statusPrazo: item.statusPrazo,
            dataNovaInspecao: item.dataNovaInspecao,
            criticidade: item.criticidade
          }));

          await this.bulkCreateInspections(inspectionsToInsert);
          console.log(`‚úÖ Loaded ${inspectionsToInsert.length} real inspection records from Excel files`);
        }
      }
    } catch (error) {
      console.warn('Could not load real inspection data:', error);
    }
  }

  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getAllInspections(): Promise<Inspection[]> {
    return await db.select().from(inspections);
  }

  async getInspectionsByFilters(filters: any): Promise<Inspection[]> {
    const conditions = [];

    if (filters.unidade && filters.unidade.length > 0) {
      conditions.push(inArray(inspections.unidadeInspecionada, filters.unidade));
    }

    if (filters.departamento && filters.departamento.length > 0) {
      conditions.push(inArray(inspections.departamento, filters.departamento));
    }

    if (filters.naoConformidade && filters.naoConformidade.length > 0) {
      conditions.push(inArray(inspections.naoConformidade, filters.naoConformidade));
    }

    // Add year filter
    if (filters.ano) {
      conditions.push(sql`EXTRACT(YEAR FROM ${inspections.dataInspecao}) = ${filters.ano}`);
    }

    // Add date range filters
    if (filters.dataInicial) {
      conditions.push(gte(inspections.dataInspecao, filters.dataInicial));
    }

    if (filters.dataFinal) {
      conditions.push(lte(inspections.dataInspecao, filters.dataFinal));
    }

    // Add status prazo filter
    if (filters.statusPrazo) {
      conditions.push(eq(inspections.statusPrazo, filters.statusPrazo));
    }

    if (conditions.length > 0) {
      return await db.select().from(inspections).where(and(...conditions));
    }

    return await db.select().from(inspections);
  }

  async createInspection(insertInspection: InsertInspection): Promise<Inspection> {
    const [inspection] = await db
      .insert(inspections)
      .values(insertInspection)
      .returning();
    return inspection;
  }

  async bulkCreateInspections(insertInspections: InsertInspection[]): Promise<Inspection[]> {
    if (insertInspections.length === 0) return [];

    // Process in smaller batches to avoid parameter limit
    const batchSize = 500;
    const results: Inspection[] = [];

    for (let i = 0; i < insertInspections.length; i += batchSize) {
      const batch = insertInspections.slice(i, i + batchSize);

      // Use upsert (insert or update) to avoid duplicates
      // Conflict resolution based on: numero + unidadeInspecionada + departamento + naoConformidade
      for (const inspection of batch) {
        const [upsertResult] = await db
          .insert(inspections)
          .values(inspection)
          .onConflictDoUpdate({
            target: [inspections.numero, inspections.unidadeInspecionada, inspections.departamento, inspections.naoConformidade],
            set: {
              dataInspecao: inspection.dataInspecao,
              delegadoCorregedor: inspection.delegadoCorregedor,
              descricaoNaoConformidade: inspection.descricaoNaoConformidade,
              providenciasIniciais: inspection.providenciasIniciais,
              providenciasIntermediarias: inspection.providenciasIntermediarias,
              providenciasConclusivas: inspection.providenciasConclusivas,
              dataInicioPrazo: inspection.dataInicioPrazo,
              diasPrazo: inspection.diasPrazo,
              dataFimRegularizacao: inspection.dataFimRegularizacao,
              statusPrazo: inspection.statusPrazo,
              dataDeterminadaNovaInspecao: inspection.dataDeterminadaNovaInspecao,
              criticidade: inspection.criticidade,
            },
          })
          .returning();

        if (upsertResult) {
          results.push(upsertResult);
        }
      }

      console.log(`Processed batch ${Math.floor(i/batchSize) + 1}/${Math.ceil(insertInspections.length/batchSize)} (${batch.length} records) with deduplication`);
    }

    // GARANTIA: Nunca criar automaticamente dados de controle de prazos
    // quando dados de inspe√ß√£o s√£o inseridos
    console.log(`‚úÖ Bulk insert de ${results.length} inspe√ß√µes conclu√≠do - SEPARA√á√ÉO TOTAL DE DADOS`);

    return results;
  }

  // Controle de Prazos methods
  async getAllControlePrazos(): Promise<ControlePrazo[]> {
    return await db.select().from(controlePrazos).orderBy(controlePrazos.dataPrazo);
  }

  async createControlePrazo(insertControlePrazo: InsertControlePrazo): Promise<ControlePrazo> {
    const [controlePrazo] = await db
      .insert(controlePrazos)
      .values(insertControlePrazo)
      .returning();
    return controlePrazo;
  }

  async updateControlePrazo(id: number, updateData: Partial<InsertControlePrazo>): Promise<ControlePrazo> {
    const [controlePrazo] = await db
      .update(controlePrazos)
      .set(updateData)
      .where(eq(controlePrazos.id, id))
      .returning();
    return controlePrazo;
  }

  async deleteControlePrazo(id: number): Promise<void> {
    await db.delete(controlePrazos).where(eq(controlePrazos.id, id));
  }

  async clearControlePrazos(): Promise<void> {
    await db.delete(controlePrazos);
  }

  async deleteAllInspections(): Promise<void> {
    await db.delete(inspections);
  }

  async deleteAllControlePrazos(): Promise<void> {
    await db.delete(controlePrazos);
  }

  async insertControlePrazosInBatch(controlePrazosData: InsertControlePrazo[]): Promise<ControlePrazo[]> {
    if (controlePrazosData.length === 0) {
      return [];
    }

    // LOG: Confirmar que este m√©todo s√≥ √© chamado para uploads expl√≠citos de controle de prazos
    console.log(`üîí CONTROLE DE PRAZOS: Inserindo ${controlePrazosData.length} registros via upload EXPL√çCITO`);

    // Process in smaller batches to avoid parameter limit
    const batchSize = 500;
    const results: ControlePrazo[] = [];

    for (let i = 0; i < controlePrazosData.length; i += batchSize) {
      const batch = controlePrazosData.slice(i, i + batchSize);

      // Use upsert to avoid duplicates based on: oficio + unidade + naoConformidade
      for (const controlePrazo of batch) {
        try {
          const [upsertResult] = await db
            .insert(controlePrazos)
            .values(controlePrazo)
            .onConflictDoUpdate({
              target: [controlePrazos.oficio, controlePrazos.unidade, controlePrazos.naoConformidade],
              set: {
                linkSei: controlePrazo.linkSei,
                dataRecebimento: controlePrazo.dataRecebimento,
                dataPrazo: controlePrazo.dataPrazo,
                status: controlePrazo.status,
                observacoes: controlePrazo.observacoes,
              },
            })
            .returning();

          if (upsertResult) {
            results.push(upsertResult);
          }
        } catch (error) {
          console.error(`Erro ao inserir controle de prazo (oficio: ${controlePrazo.oficio}, unidade: ${controlePrazo.unidade}):`, error);
          // Continue processing other records even if one fails
        }
      }

      console.log(`Processed controle de prazos batch ${Math.floor(i/batchSize) + 1}/${Math.ceil(controlePrazosData.length/batchSize)} (${batch.length} records) with deduplication`);
    }

    return results;
  }

  async bulkCreateControlePrazos(controlePrazosData: InsertControlePrazo[]): Promise<ControlePrazo[]> {
    return this.insertControlePrazosInBatch(controlePrazosData);
  }

  // Gallery methods
  async getAllPhotos(): Promise<GalleryPhoto[]> {
    return await db.select().from(galleryPhotos).orderBy(galleryPhotos.createdAt);
  }

  async getPhotosByUnidade(unidade: string): Promise<GalleryPhoto[]> {
    return await db.select().from(galleryPhotos)
      .where(eq(galleryPhotos.unidade, unidade))
      .orderBy(galleryPhotos.createdAt);
  }

  async createPhoto(insertPhoto: InsertGalleryPhoto): Promise<GalleryPhoto> {
    const [photo] = await db
      .insert(galleryPhotos)
      .values(insertPhoto)
      .returning();
    return photo;
  }

  async deletePhoto(id: number): Promise<void> {
    await db.delete(galleryPhotos).where(eq(galleryPhotos.id, id));
  }

  // Document methods
  async getAllDocuments(): Promise<Document[]> {
    return await db.select().from(documents).orderBy(documents.createdAt);
  }

  async createDocument(insertDocument: InsertDocument): Promise<Document> {
    const [document] = await db
      .insert(documents)
      .values(insertDocument)
      .returning();
    return document;
  }

  async updateDocument(id: number, updateDocument: Partial<InsertDocument>): Promise<Document> {
    const [document] = await db
      .update(documents)
      .set({
        ...updateDocument,
        updatedAt: new Date(),
      })
      .where(eq(documents.id, id))
      .returning();
    return document;
  }

  async deleteDocument(id: number): Promise<void> {
    await db.delete(documents).where(eq(documents.id, id));
  }
}

export const storage = new DatabaseStorage();

