import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Clock, FileText, ChevronLeft, ChevronRight, Filter } from "lucide-react";
import { generatePDFReport } from "@/lib/pdf-generator";

interface DeadlinesProps {
  data: any[];
}

export default function Deadlines({ data }: DeadlinesProps) {
  const [filters, setFilters] = useState({
    unidade: '',
    status: '',
    searchTerm: ''
  });
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // Get controle de prazos data
  const { data: controlePrazos = [] } = useQuery<any[]>({
    queryKey: ['/api/controle-prazos'],
  });

  // Function to calculate days remaining
  const calculateDaysRemaining = (dataPrazo: string) => {
    if (!dataPrazo) return 0;

    try {
      const today = new Date();
      let deadline;

      if (dataPrazo.includes('-') && dataPrazo.length === 10) {
        deadline = new Date(dataPrazo + 'T00:00:00');
      } else {
        deadline = new Date(dataPrazo);
      }

      today.setHours(0, 0, 0, 0);
      deadline.setHours(0, 0, 0, 0);

      const timeDiff = deadline.getTime() - today.getTime();
      const daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24));

      return daysDiff;
    } catch (error) {
      console.error('Erro ao calcular dias restantes:', error);
      return 0;
    }
  };

  // Apply filters to controle de prazos data
  const filteredData = controlePrazos.filter(item => {
    const matchesUnidade = !filters.unidade || item.unidade === filters.unidade;
    const matchesStatus = !filters.status || item.status === filters.status;
    const matchesSearch = !filters.searchTerm || 
      item.unidade.toLowerCase().includes(filters.searchTerm.toLowerCase()) ||
      item.naoConformidade.toLowerCase().includes(filters.searchTerm.toLowerCase());

    return matchesUnidade && matchesStatus && matchesSearch;
  });

  const processedData = filteredData.map(item => {
    const remainingDays = calculateDaysRemaining(item.dataPrazo);

    return {
      ...item,
      diasRestantes: remainingDays,
      statusClass: item.status === 'regularizado' ? 'default' :
                   item.status === 'nao_regularizado' ? 'destructive' :
                   remainingDays > 7 ? 'default' : 
                   remainingDays > 0 ? 'secondary' : 
                   'destructive'
    };
  });

  // Get unique values for filter dropdowns
  const uniqueUnidades = Array.from(new Set(controlePrazos.map(item => item.unidade))).sort();

  const clearFilters = () => {
    setFilters({ unidade: '', status: '', searchTerm: '' });
    setCurrentPage(1);
  };

  // Calculate pagination
  const totalPages = Math.ceil(processedData.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedData = processedData.slice(startIndex, endIndex);

  const handleGenerateReport = () => {
    generatePDFReport('deadlines', processedData);
  };

  return (
    <div className="space-y-6">
      {/* Filters Section */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Filter className="w-5 h-5 mr-2" />
            Filtros de Acompanhamento de Prazos
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <Label>Unidade</Label>
              <Select value={filters.unidade || 'all'} onValueChange={(value) => setFilters({...filters, unidade: value === 'all' ? '' : value})}>
                <SelectTrigger>
                  <SelectValue placeholder="Todas as unidades" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas as unidades</SelectItem>
                  {uniqueUnidades.map(unidade => (
                    <SelectItem key={unidade} value={unidade}>{unidade}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label>Status</Label>
              <Select value={filters.status || 'all'} onValueChange={(value) => setFilters({...filters, status: value === 'all' ? '' : value})}>
                <SelectTrigger>
                  <SelectValue placeholder="Todos os status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos os status</SelectItem>
                  <SelectItem value="pendente">Pendente</SelectItem>
                  <SelectItem value="regularizado">Regularizado</SelectItem>
                  <SelectItem value="nao_regularizado">N√£o Regularizado</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label>Busca Geral</Label>
              <Input 
                placeholder="Buscar..."
                value={filters.searchTerm}
                onChange={(e) => setFilters({...filters, searchTerm: e.target.value})}
              />
            </div>

            <div className="flex items-end">
              <Button 
                variant="outline" 
                onClick={clearFilters}
                className="w-full"
              >
                Limpar Filtros
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle className="flex items-center justify-between w-full">
            <div className="flex items-center">
              <Clock className="w-5 h-5 mr-2" />
              Acompanhamento de Prazos ({processedData.length} registros de controle)
            </div>
            <div className="flex items-center gap-4">
              {totalPages > 1 && (
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  P√°gina {currentPage} de {totalPages} 
                  <span className="text-xs">({startIndex + 1}-{Math.min(endIndex, processedData.length)} de {processedData.length})</span>
                </div>
              )}
              <Button onClick={handleGenerateReport} className="bg-police-blue hover:bg-blue-700">
                <FileText className="w-4 h-4 mr-2" />
                Relat√≥rio de Prazos
              </Button>
            </div>
          </CardTitle>
        </CardHeader>

        <CardContent>
          {controlePrazos.length === 0 ? (
            <div className="text-center py-12">
              <Clock className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">Nenhum prazo encontrado</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Unidade</TableHead>
                    <TableHead>Of√≠cio</TableHead>
                    <TableHead>N√£o Conformidade</TableHead>
                    <TableHead>Data Prazo</TableHead>
                    <TableHead>Dias Restantes</TableHead>
                    <TableHead>Status</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {paginatedData.map((item) => (
                    <TableRow key={item.id} className="hover:bg-gray-50">
                      <TableCell className="font-medium">
                        {item.unidade}
                      </TableCell>
                      <TableCell>
                        {item.oficio}
                      </TableCell>
                      <TableCell className="max-w-xs">
                        <div className="truncate" title={item.naoConformidade}>
                          {item.naoConformidade}
                        </div>
                      </TableCell>
                      <TableCell>
                        {item.dataPrazo ? 
                          new Date(item.dataPrazo).toLocaleDateString('pt-BR') : 
                          'N√£o definida'
                        }
                      </TableCell>
                      <TableCell>
                        {item.status === 'regularizado' ? (
                          <span className="text-green-600 font-medium">‚úì Regularizado</span>
                        ) : item.status === 'nao_regularizado' ? (
                          <span className="text-red-600 font-medium">‚úó N√£o Regularizado</span>
                        ) : !item.dataPrazo ? (
                          <span className="text-gray-500 font-medium">N√£o definida</span>
                        ) : (
                          item.diasRestantes > 0 
                            ? `${item.diasRestantes} dias` 
                            : `${Math.abs(item.diasRestantes)} dias em atraso`
                        )}
                      </TableCell>
                      <TableCell>
                        <Badge variant={item.statusClass}>
                          {item.status === 'pendente' ? 'Pendente' :
                           item.status === 'regularizado' ? 'Regularizado' :
                           'N√£o Regularizado'}
                        </Badge>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {/* Pagination Controls */}
              {totalPages > 1 && (
                <div className="flex items-center justify-between mt-4 px-2">
                  <div className="text-sm text-gray-600">
                    Mostrando {startIndex + 1} a {Math.min(endIndex, processedData.length)} de {processedData.length} registros
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                      disabled={currentPage === 1}
                    >
                      <ChevronLeft className="w-4 h-4" />
                      Anterior
                    </Button>

                    <div className="flex items-center gap-1">
                      {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                        let pageNum;
                        if (totalPages <= 5) {
                          pageNum = i + 1;
                        } else if (currentPage <= 3) {
                          pageNum = i + 1;
                        } else if (currentPage >= totalPages - 2) {
                          pageNum = totalPages - 4 + i;
                        } else {
                          pageNum = currentPage - 2 + i;
                        }

                        return (
                          <Button
                            key={pageNum}
                            variant={currentPage === pageNum ? "default" : "outline"}
                            size="sm"
                            onClick={() => setCurrentPage(pageNum)}
                            className="w-8 h-8 p-0"
                          >
                            {pageNum}
                          </Button>
                        );
                      })}
                    </div>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                      disabled={currentPage === totalPages}
                    >
                      Pr√≥xima
                      <ChevronRight className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/gallery-database.t.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Images, Upload, X, ChevronLeft, ChevronRight, Search, Maximize2 } from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface GalleryProps {
  data: any[];
  filters: any;
}

interface GalleryPhoto {
  id: number;
  unidade: string;
  fileName: string;
  fileType: string;
  fileSize: number;
  imageData: string;
  createdAt: string;
}

export default function GalleryDatabase({ data = [], filters }: GalleryProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedImage, setSelectedImage] = useState<GalleryPhoto | null>(null);
  const itemsPerPage = 9;
  const queryClient = useQueryClient();

  // Buscar fotos do banco de dados
  const { data: photos = [], isLoading: photosLoading } = useQuery({
    queryKey: ['/api/gallery/photos'],
    refetchOnWindowFocus: false,
  });

  // Mutation para salvar foto
  const savePhotoMutation = useMutation({
    mutationFn: async (photoData: {
      unidade: string;
      fileName: string;
      fileType: string;
      fileSize: number;
      imageData: string;
    }) => {
      console.log('Enviando dados para API:', {
        unidade: photoData.unidade,
        fileName: photoData.fileName,
        fileType: photoData.fileType,
        fileSize: photoData.fileSize,
        imageSizeKB: Math.round(photoData.imageData.length / 1024)
      });

      const response = await fetch('/api/gallery/photos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(photoData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Erro da API:', response.status, errorText);
        throw new Error(`Erro ${response.status}: ${errorText}`);
      }

      const result = await response.json();
      console.log('Foto salva com sucesso:', result);
      return result;
    },
    onSuccess: (data) => {
      console.log('Foto salva, invalidando cache...');
      queryClient.invalidateQueries({ queryKey: ['/api/gallery/photos'] });
      alert(`Foto ${data.fileName} salva com sucesso!`);
    },
    onError: (error) => {
      console.error('Erro ao salvar foto:', error);
      alert(`Erro ao salvar foto: ${error.message}`);
    },
  });

  // Mutation para deletar foto
  const deletePhotoMutation = useMutation({
    mutationFn: async (photoId: number) => {
      const response = await apiRequest(`/api/gallery/photos/${photoId}`, 'DELETE');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/gallery/photos'] });
    },
    onError: (error) => {
      console.error('Erro ao deletar foto:', error);
      alert('Erro ao deletar foto. Tente novamente.');
    },
  });

  // Comprimir e converter arquivo para base64
  const compressAndConvertFile = (file: File): Promise<string> => {
    return new Promise((resolve, reject) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const img = new Image();

      img.onload = () => {
        // Definir tamanho m√°ximo para compress√£o
        const maxWidth = 800;
        const maxHeight = 600;

        let { width, height } = img;

        // Calcular nova dimens√£o mantendo propor√ß√£o
        if (width > height) {
          if (width > maxWidth) {
            height = (height * maxWidth) / width;
            width = maxWidth;
          }
        } else {
          if (height > maxHeight) {
            width = (width * maxHeight) / height;
            height = maxHeight;
          }
        }

        canvas.width = width;
        canvas.height = height;

        // Desenhar imagem redimensionada
        ctx?.drawImage(img, 0, 0, width, height);

        // Converter para base64 com qualidade reduzida
        const compressedBase64 = canvas.toDataURL('image/jpeg', 0.7);
        console.log('Imagem comprimida:', file.name, 'tamanho original:', file.size, 'tamanho base64:', compressedBase64.length);
        resolve(compressedBase64);
      };

      img.onerror = reject;
      img.src = URL.createObjectURL(file);
    });
  };

  // Upload de arquivos
  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>, unidade: string) => {
    console.log('Upload para unidade:', unidade);

    const files = Array.from(event.target.files || []);
    console.log('Arquivos selecionados:', files.length);

    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png'];

    for (const file of files) {
      console.log('Processando:', file.name, file.type, file.size);

      if (allowedTypes.includes(file.type)) {
        try {
          const base64Url = await compressAndConvertFile(file);
          console.log('Base64 gerado:', file.name, base64Url.length, 'chars');

          // Salvar no banco de dados
          await savePhotoMutation.mutateAsync({
            unidade,
            fileName: file.name,
            fileType: file.type,
            fileSize: file.size,
            imageData: base64Url
          });

          console.log('Foto salva:', file.name);
        } catch (error) {
          console.error('Erro ao processar:', error);
          alert(`Erro ao salvar ${file.name}`);
        }
      } else {
        alert('Apenas arquivos JPG, JPEG e PNG s√£o permitidos.');
      }
    }

    event.target.value = '';
  };

  // Remover foto
  const removePhoto = async (photoId: number) => {
    if (confirm('Tem certeza que deseja remover esta foto?')) {
      try {
        await deletePhotoMutation.mutateAsync(photoId);
      } catch (error) {
        console.error('Erro ao remover:', error);
      }
    }
  };

  // Dados seguros
  const safeData = Array.isArray(data) ? data : [];

  // Agrupar por unidade (apenas um card por unidade)
  const unitMap = new Map();
  safeData.forEach(item => {
    if (!item || !item.unidadeInspecionada) return;

    if (!unitMap.has(item.unidadeInspecionada)) {
      unitMap.set(item.unidadeInspecionada, {
        ...item,
        totalInspections: 1,
        allInspections: [item]
      });
    } else {
      const existing = unitMap.get(item.unidadeInspecionada);
      existing.totalInspections += 1;
      existing.allInspections.push(item);
      // Manter a inspe√ß√£o mais recente como representativa
      if (new Date(item.dataInspecao) > new Date(existing.dataInspecao)) {
        Object.assign(existing, item);
        existing.totalInspections = existing.allInspections.length;
      }
    }
  });

  // Converter para array e filtrar
  const uniqueUnits = Array.from(unitMap.values());

  const filteredData = uniqueUnits.filter(item => {
    if (!item) return false;

    if (searchTerm === '') return true;

    const searchLower = searchTerm.toLowerCase();
    return (
      (item.unidadeInspecionada && item.unidadeInspecionada.toLowerCase().includes(searchLower)) ||
      (item.departamento && item.departamento.toLowerCase().includes(searchLower)) ||
      (item.numero && item.numero.toString().includes(searchTerm))
    );
  });

  // Pagina√ß√£o
  const totalItems = filteredData.length;
  const totalPages = Math.ceil(totalItems / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedData = filteredData.slice(startIndex, endIndex);

  // Reset p√°gina quando busca muda
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm]);

  if (photosLoading) {
    return (
      <div className="flex justify-center items-center p-8">
        <div className="text-gray-500">Carregando galeria...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center">
              <Images className="w-5 h-5 mr-2 text-purple-600" />
              Galeria de Unidades PCBA ({totalItems} unidades ‚Ä¢ {photos.length} fotos)
            </div>
            {totalPages > 1 && (
              <div className="text-sm text-gray-600">
                P√°gina {currentPage} de {totalPages}
              </div>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {/* Busca */}
          <div className="mb-6">
            <div className="relative max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                type="text"
                placeholder="Buscar unidades..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
            {searchTerm && (
              <div className="mt-2 flex items-center gap-2">
                <Button onClick={() => setSearchTerm('')} variant="outline" size="sm">
                  <X className="w-4 h-4 mr-1" />
                  Limpar
                </Button>
                <span className="text-sm text-gray-600">
                  {totalItems} resultado{totalItems !== 1 ? 's' : ''}
                </span>
              </div>
            )}
          </div>

          {totalItems === 0 ? (
            <div className="text-center py-12">
              <Images className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">
                {searchTerm ? "Nenhuma unidade encontrada" : "Nenhuma unidade dispon√≠vel"}
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {paginatedData.map(unit => {
                // Filtrar fotos para esta unidade espec√≠fica
                const unitPhotos = photos.filter((photo: GalleryPhoto) => 
                  photo.unidade === unit.unidadeInspecionada
                );

                return (
                  <Card key={unit.unidadeInspecionada} className="border-2 hover:border-purple-200 transition-all">
                    <div className="min-h-[200px] bg-gradient-to-br from-purple-50 to-blue-50 rounded-t-lg flex items-center justify-center relative">
                      {unitPhotos.length > 0 ? (
                        <div className={`grid gap-1 w-full h-full p-2 ${
                          unitPhotos.length === 1 ? 'grid-cols-1' :
                          unitPhotos.length === 2 ? 'grid-cols-2' :
                          unitPhotos.length <= 4 ? 'grid-cols-2' :
                          unitPhotos.length <= 9 ? 'grid-cols-3' :
                          'grid-cols-4'
                        }`}>
                          {unitPhotos.map((photo) => (
                            <div key={photo.id} className="relative group overflow-hidden rounded">
                              <div className="aspect-square bg-gray-100 overflow-hidden rounded">
                                <img 
                                  src={photo.imageData} 
                                  alt={photo.fileName} 
                                  className="w-full h-full object-cover cursor-pointer hover:scale-105 transition-transform" 
                                  onClick={() => setSelectedImage(photo)}
                                />
                              </div>
                              <button
                                onClick={(e) => {
                                  e.stopPropagation();
                                  removePhoto(photo.id);
                                }}
                                className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 text-white rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity text-xs z-10"
                                title="Remover foto"
                              >
                                √ó
                              </button>
                              <button
                                onClick={(e) => {
                                  e.stopPropagation();
                                  setSelectedImage(photo);
                                }}
                                className="absolute bottom-1 right-1 w-5 h-5 bg-black bg-opacity-50 text-white rounded flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity"
                                title="Ampliar foto"
                              >
                                <Maximize2 className="w-3 h-3" />
                              </button>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="text-center text-gray-500">
                          <Images className="w-12 h-12 mx-auto mb-2" />
                          <p className="text-sm">{unit.unidadeInspecionada}</p>
                          <p className="text-xs">Sem fotos</p>
                        </div>
                      )}
                    </div>

                    <CardContent className="p-4">
                      <h4 className="font-medium text-gray-900 mb-1">
                        {unit.unidadeInspecionada}
                      </h4>

                      <p className="text-sm text-gray-600 mb-1">
                        {unit.departamento}
                      </p>

                      <p className="text-xs text-gray-500 mb-3">
                        {unit.totalInspections} inspe√ß√µes ‚Ä¢ {unitPhotos.length} fotos
                      </p>

                      <div className="relative">
                        <input
                          type="file"
                          multiple
                          accept=".jpg,.jpeg,.png"
                          onChange={(e) => handleFileUpload(e, unit.unidadeInspecionada)}
                          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                          id={`upload-${unit.unidadeInspecionada.replace(/\s+/g, '-')}`}
                        />
                        <Button 
                          size="sm" 
                          variant="outline" 
                          className="w-full"
                          disabled={savePhotoMutation.isPending}
                        >
                          <Upload className="w-4 h-4 mr-2" />
                          {savePhotoMutation.isPending ? 'Salvando...' : 'Upload JPG/PNG'}
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}

          {/* Pagina√ß√£o */}
          {totalPages > 1 && (
            <div className="flex items-center justify-center gap-2 mt-8">
              <Button
                onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                disabled={currentPage === 1}
                variant="outline"
                size="sm"
              >
                <ChevronLeft className="w-4 h-4 mr-1" />
                Anterior
              </Button>

              <div className="flex items-center gap-1">
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  const pageNum = Math.max(1, currentPage - 2) + i;
                  if (pageNum > totalPages) return null;

                  return (
                    <Button
                      key={pageNum}
                      onClick={() => setCurrentPage(pageNum)}
                      variant={currentPage === pageNum ? "default" : "outline"}
                      size="sm"
                      className="w-10"
                    >
                      {pageNum}
                    </Button>
                  );
                })}
              </div>

              <Button
                onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                disabled={currentPage === totalPages}
                variant="outline"
                size="sm"
              >
                Pr√≥xima
                <ChevronRight className="w-4 h-4 ml-1" />
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Modal para ampliar imagem */}
      <Dialog open={!!selectedImage} onOpenChange={() => setSelectedImage(null)}>
        <DialogContent className="max-w-4xl">
          <DialogHeader>
            <DialogTitle>{selectedImage?.fileName}</DialogTitle>
          </DialogHeader>
          {selectedImage && (
            <div className="flex justify-center">
              <img 
                src={selectedImage.imageData} 
                alt={selectedImage.fileName}
                className="max-w-full max-h-[70vh] object-contain rounded"
              />
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

